val MyActionGroupId = "EditorPopupMenu"

registerAction(id = "CHS To CHT", actionGroupId = MyActionGroupId, callback = { event: AnActionEvent ->
	val project = event.project!!
	val editor = project.currentEditor!!
	editor.document.runWriteAction(project, description = "", callback = {
		//val offset = editor.caretModel.offset
		//val currentLine = editor.caretModel.logicalPosition.line
		//val lineStartOffset = editor.document.getLineStartOffset(currentLine)
		//val selectionInfo = editor.getCaretModel().getCaretsAndSelections().get(0)

		val model: SelectionModel = editor.getSelectionModel();
		var selectedText: String? = model.getSelectedText();

		if (selectedText!!.isNotEmpty())
		{
			val r = Encoding().StoT(selectedText)

			//show("${selectedText}\n${r}")

			if (!selectedText.equals(r))
			{
				replaceSelectionText(editor, model, r)
			}
		}
	})
})

