	/**
	 * 簡易版 簡轉繁
	 */
	fun StoT(data: String): String
	{
		return translate(data, S2T)
	}

	/**
	 * 簡易版 繁轉簡
	 */
	fun TtoS(data: String): String
	{
		return translate(data, T2S)
	}

	/**
	 * 簡轉繁
	 */
	fun StoT2(data: String): String
	{
		return translate(data, S2T2)
	}

	/**
	 * 繁轉簡
	 */
	fun TtoS2(data: String): String
	{
		return translate(data, T2S2)
	}

