		fun build(table: HashMap<Int, Int>): HashMap<Int, Int>
		{
			var table2 = table ?: HashMap<Int, Int>()
			table2.clear()

			var a1 = arr_from
			var a2 = arr_to

			val n = Math.min(a1.size, a2.size)
			var i = 0

			while (i < n)
			{
				val cT = a1[i]
				val cS = a2[i]

				table2.put(cT, cS)

				i++
			}

			return table2;
		}

		fun toUnicodeCharArray(s: String): IntArray
		{
			var len = s.length
			var len2 = s.codePointCount(0, s.length)
			var i = 0

			var p1 = s.codePointAt(0)
			var p2 = s.codePointAt(2)

			var c1 = Character.toChars(p1)
			var c2 = Character.toChars(p2)

			var ca = IntArray(len2)
			var ci = 0

			while (i < len)
			{
				val p = s.codePointAt(i)
				var c = Character.toChars(p)

				ca.set(ci, p)
				ci++

				i += Character.charCount(p)
			}

			return ca;
		}
	}

